import React from "react";
import { connect } from "react-redux";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import CourseBackground from './styled-components/courseBackground';

const mapStateToProps = state => {
  return {
    cursos: state.data.todososdados
  };
};

const settings = {
  dots: false,
  infinite: true,
  speed: 500,
  slidesToShow: 4,
  slidesToScroll: 4,
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 3,
        infinite: true,
        dots: false
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2,
        initialSlide: 2
      }
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1
      }
    }
  ]
};



function ConnectedList({ cursos }) {
  if (!cursos.length) {
    return (
      <div className="centerText">
        <img alt="loadingSpinner" src="data:image/gif;base64," />
      </div>
    )
  }
  return (
    <Slider {...settings}>
      {cursos[0].map(function (curso) {
        return (
          <CourseBackground className="courseBg" key={curso.id}>
            <p>{curso.nome}</p>
            <p className="courseDesc">{curso.area}</p>
          </CourseBackground>
        );
      })}
    </Slider>
  );
}

const CoursesList = connect(mapStateToProps)(ConnectedList);
export default CoursesList;