import React from 'react';
import { userService } from '../../services/user.service';
import Container from 'react-bootstrap/Container'

class Login extends React.Component {
    constructor(props) {
        super(props);

        userService.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
            loading: false,
            error: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;

        // stop here if form is invalid
        if (!(username && password)) {
            return;
        }

        this.setState({ loading: true });
        userService.login(username, password)
            .then(
                user => {
                    const { from } = this.props.location.state || { from: { pathname: "/logged" } };
                    this.props.history.push(from);
                    window.location.reload()
                },
                error => this.setState({ error, loading: false })
            );
    }

    render() {
        const { username, password, submitted, loading, error } = this.state;
        return (
            <Container className="loginContainer">
                <div className="alert alert-info">
                    Usuário: joao<br />
                    Senha: sanar
                </div>
                <h2>Bem vindo!</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <input type="text" className="form-control" placeholder="Usuário" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username && <div className="help-block">Necessário inserir o usuário</div>}
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <input type="password" className="form-control" placeholder="Senha" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password && <div className="help-block">Necessário inserir a senha</div>}
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading}>Entrar</button>
                        {loading && <img alt="loadingSpinner" src="data:image/gif;base64," />}
                    </div>
                    {error && <div className={'alert alert-danger'}>{error}</div>}
                </form>
            </Container>
        );
    }
}

export default Login;